func (srs *StepRunSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := srs.sqlQuery().Query()
	if err := srs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (srs *StepRunSelect) sqlQuery() sql.Querier {
	selector := srs.sql
	selector.Select(selector.Columns(srs.fields...)...)
	return selector
}
